import React, { useEffect, useState } from 'react';

// Set this to your backend URL
const BACKEND_URL = 'http://127.0.0.1:3001';

function App() {
  const [loggedIn, setLoggedIn] = useState(null);
  const [Profile, setProfile] = useState(null);
  const [loadingProfile, setLoadingProfile] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch(`${BACKEND_URL}/api/auth/status`, {
      credentials: 'include',
    })
      .then(res => res.json())
      .then(data => setLoggedIn(data.loggedIn))
      .catch(() => setLoggedIn(false));
  }, []);

  const handleLogout = () => {
    fetch(`${BACKEND_URL}/auth/logout`, {
      method: 'POST',
      credentials: 'include',
    })
      .then(() => setLoggedIn(false))
      .catch(console.error);
  };

  const handleLoadProfile = () => {
    setLoadingProfile(true);
    fetch(`${BACKEND_URL}/api/spotify/profile`, {
      credentials: 'include',
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch profile');
        return res.json();
      })
      .then(data => {
        setProfile(data);
        setError('');
      })
      .catch(err => {
        console.error(err);
        setError('Failed to load profile');
      })
      .finally(() => {
        setLoadingProfile(false);
      });
  };

  if (loggedIn === null) return <p>Loading...</p>;

  if (!loggedIn) {
    return (
      <div>
        <h2>You are not logged in.</h2>
        <a href={`${BACKEND_URL}/auth/login`}>
          <button>Login with Spotify</button>
        </a>
      </div>
    );
  }

  return (
    <div>
      <h1>Welcome, you are logged in!</h1>
      <button onClick={handleLoadProfile} disabled={loadingProfile}>
        {loadingProfile ? 'Loading Profile...' : 'Load Profile'}
      </button>
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}

      {profile && (
        <div style={{ marginTop: '20px' }}>
          <h2>{profile.display_name}</h2>
          {profile.images && profile.images.length > 0 && (
            <img
              src={profile.images[0].url}
              alt="Profile"
              width={100}
              style={{ borderRadius: '50%' }}
            />
          )}
          <p>Email: {profile.email}</p>
          <p>Country: {profile.country}</p>
          <p>Followers: {profile.followers.total}</p>
        </div>
      )}

      <button onClick={handleLogout} style={{ marginTop: '20px' }}>
        Log out
      </button>
    </div>
  );
}

export default App;
