import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  Link
} from 'react-router-dom';

function LoginPage({ error}) {
  return (
    <div>
      <h2>You are not logged in.</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <a href="http://localhost:3001/auth/login">
        <button>Login with Spotify</button>
      </a>
    </div>
  );
}

function Home() {
  return (
    <div>
      <h1>Welcome, you are logged in!</h1>
      <p>You can now explore Spotify features.</p>
      <Link to="/">Log out</Link>
    </div>
  );
}

function App() {
  const [loggedIn, setLoggedIn] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch('https://9369677ba549.ngrok-free.app/api/auth/status', { credentials: 'include' })
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to check login status');
        }
        return res.json();
      })
      .then(data => {
        if (data.loggedIn) {
          setLoggedIn(true);
          setError('');
        } else {
          setLoggedIn(false);
          setError('You are not logged in. Please log in to continue.');
        }
      })
      .catch(() => {
        setLoggedIn(false);
        setError('Error checking login status. Please try again.');
      });
  }, []);

  if (loggedIn === null) return <p>Loading...</p>;

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={loggedIn ? <Navigate to="/home" replace /> : <LoginPage error={error} />}
        />
        <Route
          path="/home"
          element={loggedIn ? <Home /> : <Navigate to="/" replace />}
        />
      </Routes>
    </Router>
  );
}

export default App;
