import React, { useState, useEffect } from 'react';
import { useAuthStatus } from './hooks/useAuthStatus';
import LoadingButton from './components/LoadingButton';
import TopTracksList from './components/TopTracksList';
import TopArtistsList from './components/TopArtistsList';
import FetchOptions from './components/FetchOptions';
import ProfileHeader from './components/ProfileHeader';
import RecentlyPlayedList from './components/RecentlyPlayedList';
import RecentlyPlayedOptions from './components/RecentlyPlayedOptions';
import PlaylistsList from './components/PlaylistsList';

const BACKEND_URL = 'http://127.0.0.1:3001';
const DEFAULT_LIMIT = 10;
const DEFAULT_TIME_RANGE = 'medium_term';
const DEFAULT_RECENT_LIMIT = 10;

function App() {
  const { loggedIn, logout } = useAuthStatus();
  const [data, setData] = useState(null);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isRedirecting, setIsRedirecting] = useState(false);
  const [limit, setLimit] = useState(DEFAULT_LIMIT);
  const [timeRange, setTimeRange] = useState(DEFAULT_TIME_RANGE);
  const [recentLimit, setRecentLimit] = useState(DEFAULT_RECENT_LIMIT);

  useEffect(() => {
    if (loggedIn === false && !isRedirecting) {
      setIsRedirecting(true);
      window.location.href = `${BACKEND_URL}/auth/login`;
    }
  }, [loggedIn, isRedirecting]);

  useEffect(() => {
    if (loggedIn) {
      fetchProfile();
    }
  }, [loggedIn]);

  const fetchProfile = async () => {
    try {
      const response = await fetch(`${BACKEND_URL}/api/profile`, { 
        credentials: 'include' 
      });
      const result = await response.json();
      setProfile(result);
    } catch (err) {
      console.error('Error fetching profile:', err);
    }
  };

  const fetchData = async (endpoint, params = {}) => {
    setLoading(true);
    setError(null);
    try {
      const queryString = new URLSearchParams(params).toString();
      const url = `${BACKEND_URL}/api${endpoint}${queryString ? `?${queryString}` : ''}`;
      const response = await fetch(url, { credentials: 'include' });
      const result = await response.json();
      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRecentlyPlayed = () => {
    fetchData('/recently-played', { limit: recentLimit });
  };

  // Show loading state while checking auth
  if (loggedIn === null) {
    return <div>Loading...</div>;
  }

  // Don't render anything while redirecting
  if (!loggedIn || isRedirecting) {
    return null;
  }

  const renderData = () => {
    if (!data) return null;

    // Handle playlist data - check for items array and type
    if (data.items && Array.isArray(data.items)) {
      const firstItem = data.items[0];
      if (firstItem && firstItem.type === 'playlist') {
        return <PlaylistsList playlists={data.items} />;
      }
    }

    // Check if we have an array of items
    if (Array.isArray(data) && data.length > 0) {
      if (data[0].type === 'artist') {
        return <TopArtistsList artists={data} />;
      }
      if (data[0].type === 'track') {
        return <TopTracksList tracks={data} />;
      }
      if (data[0].track) {
        return <RecentlyPlayedList items={data} />;
      }
    }

    // Default JSON view for unhandled data types
    return (
      <pre style={{ 
        maxHeight: '400px', 
        overflow: 'auto', 
        backgroundColor: '#f5f5f5', 
        padding: '1rem' 
      }}>
        {JSON.stringify(data, null, 2)}
      </pre>
    );
  };

  const renderOptions = () => {
    if (!data) return null;

    if (Array.isArray(data) && data.length > 0) {
      // Show FetchOptions only for top artists
      if (data[0].type === 'artist') {
        return (
          <FetchOptions 
            limit={limit} 
            timeRange={timeRange} 
            onLimitChange={setLimit} 
            onTimeRangeChange={setTimeRange} 
            onRefresh={(params) => fetchData('/top-artists', params)}
          />
        );
      }
      if (data[0].type === 'track') {
        return (
          <FetchOptions 
            limit={limit} 
            timeRange={timeRange} 
            onLimitChange={setLimit} 
            onTimeRangeChange={setTimeRange} 
            onRefresh={(params) => fetchData('/top-tracks', params)}
          />
        );
      }
      if (data[0].type === 'playlist') {
        return (
          <RecentlyPlayedOptions
            limit={limit}
            onLimitChange={setLimit}
            onRefresh={(params) => fetchData('/playlists', params)}
          />
        );
      }
      // Show RecentlyPlayedOptions only for recently played tracks
      if (data[0].track) {
        return (
          <RecentlyPlayedOptions
            limit={recentLimit}
            onLimitChange={setRecentLimit}
            onRefresh={(params) => fetchData('/recently-played', params)}
          />
        );
      }
    }
    return null;
  };

  return (
    <div style={{ 
      padding: '2rem',
      backgroundColor: '#121212',
      minHeight: '100vh',
      color: 'white'
    }}>
      <ProfileHeader profile={profile} />

      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '2rem',
        backgroundColor: '#181818',
        borderRadius: '8px',
        boxShadow: '0 4px 60px rgba(0,0,0,0.5)'
      }}>
        <h1 style={{ 
          fontSize: '2.5rem', 
          marginBottom: '2rem',
          color: '#1DB954'
        }}>
          Spotify Data Explorer
        </h1>

        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
          gap: '1.5rem',
          marginBottom: '2rem'
        }}>
          <button 
            onClick={() => fetchData('/top-artists', { limit, time_range: timeRange })}
            style={{
              padding: '1.5rem',
              backgroundColor: '#1DB954',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              fontSize: '1.1rem',
              fontWeight: 'bold',
              cursor: 'pointer',
              transition: 'transform 0.2s, background-color 0.2s',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '0.5rem'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.backgroundColor = '#1ed760';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.backgroundColor = '#1DB954';
            }}
            disabled={loading}
          >
            <span>üé∏ Top Artists</span>
            <span style={{ fontSize: '0.9rem', fontWeight: 'normal' }}>
              Discover your most played artists
            </span>
          </button>

          <button 
            onClick={() => fetchData('/top-tracks', { limit, time_range: timeRange })}
            style={{
              padding: '1.5rem',
              backgroundColor: '#1DB954',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              fontSize: '1.1rem',
              fontWeight: 'bold',
              cursor: 'pointer',
              transition: 'transform 0.2s, background-color 0.2s',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '0.5rem'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.backgroundColor = '#1ed760';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.backgroundColor = '#1DB954';
            }}
            disabled={loading}
          >
            <span>üéµ Top Tracks</span>
            <span style={{ fontSize: '0.9rem', fontWeight: 'normal' }}>
              View your favorite songs
            </span>
          </button>

          <button 
            onClick={() => fetchData('/recently-played', { limit: recentLimit })}
            style={{
              padding: '1.5rem',
              backgroundColor: '#1DB954',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              fontSize: '1.1rem',
              fontWeight: 'bold',
              cursor: 'pointer',
              transition: 'transform 0.2s, background-color 0.2s',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '0.5rem'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.backgroundColor = '#1ed760';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.backgroundColor = '#1DB954';
            }}
            disabled={loading}
          >
            <span>‚è±Ô∏è Recently Played</span>
            <span style={{ fontSize: '0.9rem', fontWeight: 'normal' }}>
              See your listening history
            </span>
          </button>

          <button 
            onClick={() => fetchData('/playlists', { limit: 20, offset: 0 })}
            style={{
              padding: '1.5rem',
              backgroundColor: '#1DB954',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              fontSize: '1.1rem',
              fontWeight: 'bold',
              cursor: 'pointer',
              transition: 'transform 0.2s, background-color 0.2s',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '0.5rem'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.backgroundColor = '#1ed760';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.backgroundColor = '#1DB954';
            }}
            disabled={loading}
          >
            <span>üìë Playlists</span>
            <span style={{ fontSize: '0.9rem', fontWeight: 'normal' }}>
              Browse your playlists
            </span>
          </button>
        </div>

        {error && (
          <div style={{ 
            color: '#ff4444',
            backgroundColor: 'rgba(255,68,68,0.1)',
            padding: '1rem',
            borderRadius: '4px',
            marginBottom: '1rem'
          }}>
            Error: {error}
          </div>
        )}

        {renderOptions()}
        {renderData()}

        <button 
          onClick={logout}
          style={{ 
            marginTop: '2rem',
            padding: '0.75rem 1.5rem',
            backgroundColor: 'transparent',
            border: '2px solid #ff4444',
            borderRadius: '20px',
            color: '#ff4444',
            cursor: 'pointer',
            transition: 'all 0.2s',
            fontSize: '0.9rem'
          }}
          onMouseOver={(e) => {
            e.target.style.backgroundColor = '#ff444420';
          }}
          onMouseOut={(e) => {
            e.target.style.backgroundColor = 'transparent';
          }}
        >
          Logout
        </button>
      </div>
    </div>
  );
}

export default App;
