import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  Link
} from 'react-router-dom';

// Set this to your backend LocalTunnel HTTPS URL
const BACKEND_URL = 'http://127.0.0.1:3001';

function LoginPage() {
  return (
    <div>
      <h2>You are not logged in.</h2>
      {/* Use backend LocalTunnel URL for Spotify login */}
      <a href={`${BACKEND_URL}/auth/login`}>
        <button>Login with Spotify</button>
      </a>
    </div>
  );
}

function Home() {
  const handleLogout = () => {
    fetch(`${BACKEND_URL}/auth/logout`, {
      method: 'POST',
      credentials: 'include',
    })
      .then(res => res.json())
      .then(() => {
        setLoggedIn(false); // Update state to reflect logged-out status
      })
      .catch(err => {
        console.error('Error logging out:', err);
      });
  };

  return (
    <div>
      <h1>Welcome, you are logged in!</h1>
      <p>You can now explore Spotify features.</p>
      <button onClick={handleLogout}>Log out</button>
    </div>
  );
}

function App() {
  const [loggedIn, setLoggedIn] = useState(null);

  useEffect(() => {
    fetch(`${BACKEND_URL}/api/auth/status`, {
      credentials: 'include', // important to send cookies
    })
      .then(res => res.json())
      .then(data => setLoggedIn(data.loggedIn))
      .catch(() => setLoggedIn(false));
  }, []);

  if (loggedIn === null) return <p>Loading...</p>;

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={loggedIn ? <Navigate to="/home" replace /> : <LoginPage />}
        />
        <Route
          path="/home"
          element={loggedIn ? <Home /> : <Navigate to="/" replace />}
        />
      </Routes>
    </Router>
  );
}

export default App;
