import React, { useEffect, useState } from 'react';

function App() {
  const [loggedIn, setLoggedIn] = useState(null); // null = loading, true/false = known

  useEffect(() => {
    fetch('http://localhost:3001/api/auth/status', { credentials: 'include' })
      .then(res => res.json())
      .then(data => setLoggedIn(data.loggedIn))
      .catch(() => setLoggedIn(false));
  }, []);

  if (loggedIn === null) {
    return <p>Loading...</p>;
  }

  if (!loggedIn) {
    return (
      <div>
        <h2>You are not logged in.</h2>
        <a href="http://localhost:3001/auth/login">
          <button>Login with Spotify</button>
        </a>
      </div>
    );
  }

  return (
    <div>
      <h1>Welcome, you are logged in!</h1>
      <p>Now you can explore Spotify features through the app.</p>
    </div>
  );
}

export default App;
