import React from 'react';

const PlaylistsList = ({ playlists }) => {
  if (!playlists) return null;

  return (
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
      gap: '2rem',
      padding: '1rem'
    }}>
      {playlists.map((playlist) => (
        <div
          key={playlist.id}
          style={{
            backgroundColor: '#282828',
            borderRadius: '8px',
            padding: '1rem',
            color: 'white',
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          <img
            src={playlist.images[0]?.url || '/default-playlist.png'}
            alt={playlist.name}
            style={{
              width: '100%',
              aspectRatio: '1',
              objectFit: 'cover',
              borderRadius: '4px',
              marginBottom: '1rem'
            }}
          />
          <h3 style={{ 
            margin: '0 0 0.5rem 0',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            whiteSpace: 'nowrap'
          }}>
            {playlist.name}
          </h3>
          <div style={{ 
            fontSize: '0.9rem', 
            color: '#b3b3b3',
            marginBottom: '0.5rem' 
          }}>
            By {playlist.owner.display_name}
          </div>
          <div style={{ 
            backgroundColor: '#1DB954',
            padding: '0.25rem 0.5rem',
            borderRadius: '12px',
            fontSize: '0.8rem',
            display: 'inline-block',
            marginBottom: 'auto'
          }}>
            {playlist.tracks.total} tracks
          </div>
          <a
            href={playlist.uri}
            style={{
              color: '#1DB954',
              textDecoration: 'none',
              padding: '0.5rem',
              border: '1px solid #1DB954',
              borderRadius: '4px',
              textAlign: 'center',
              marginTop: '1rem',
              transition: 'all 0.2s'
            }}
            onMouseOver={(e) => e.target.style.backgroundColor = '#1DB95420'}
            onMouseOut={(e) => e.target.style.backgroundColor = 'transparent'}
          >
            Open in Spotify
          </a>
        </div>
      ))}
    </div>
  );
};

export default PlaylistsList;
