import React, { useState, useEffect } from 'react';
import { useAuthStatus } from './hooks/useAuthStatus';
import LoadingButton from './components/LoadingButton';
import TopTracksList from './components/TopTracksList';
import TopArtistsList from './components/TopArtistsList';
import FetchOptions from './components/FetchOptions';
import ProfileHeader from './components/ProfileHeader';
import RecentlyPlayedList from './components/RecentlyPlayedList';
import RecentlyPlayedOptions from './components/RecentlyPlayedOptions';

const BACKEND_URL = 'http://127.0.0.1:3001';
const DEFAULT_LIMIT = 10;
const DEFAULT_TIME_RANGE = 'medium_term';
const DEFAULT_RECENT_LIMIT = 10;
const DEFAULT_BEFORE_DATE = new Date().toISOString().slice(0, 16); // Current time
const DEFAULT_AFTER_DATE = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().slice(0, 16); // 7 days ago

function App() {
  const { loggedIn, logout } = useAuthStatus();
  const [data, setData] = useState(null);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isRedirecting, setIsRedirecting] = useState(false);
  const [limit, setLimit] = useState(DEFAULT_LIMIT);
  const [timeRange, setTimeRange] = useState(DEFAULT_TIME_RANGE);
  const [recentLimit, setRecentLimit] = useState(DEFAULT_RECENT_LIMIT);
  const [beforeDate, setBeforeDate] = useState(DEFAULT_BEFORE_DATE);
  const [afterDate, setAfterDate] = useState(DEFAULT_AFTER_DATE);

  useEffect(() => {
    if (loggedIn === false && !isRedirecting) {
      setIsRedirecting(true);
      window.location.href = `${BACKEND_URL}/auth/login`;
    }
  }, [loggedIn, isRedirecting]);

  useEffect(() => {
    if (loggedIn) {
      fetchProfile();
    }
  }, [loggedIn]);

  const fetchProfile = async () => {
    try {
      const response = await fetch(`${BACKEND_URL}/api/profile`, { 
        credentials: 'include' 
      });
      const result = await response.json();
      setProfile(result);
    } catch (err) {
      console.error('Error fetching profile:', err);
    }
  };

  const fetchData = async (endpoint, params = {}) => {
    setLoading(true);
    setError(null);
    try {
      const queryString = new URLSearchParams(params).toString();
      const url = `${BACKEND_URL}/api${endpoint}${queryString ? `?${queryString}` : ''}`;
      const response = await fetch(url, { credentials: 'include' });
      const result = await response.json();
      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Show loading state while checking auth
  if (loggedIn === null) {
    return <div>Loading...</div>;
  }

  // Don't render anything while redirecting
  if (!loggedIn || isRedirecting) {
    return null;
  }

  const renderData = () => {
    if (!data) return null;

    if (Array.isArray(data) && data.length > 0) {
      if (data[0].type === 'artist') {
        return <TopArtistsList artists={data} />;
      }
      if (data[0].track) { // Recently played items have a 'track' property
        return <RecentlyPlayedList items={data} />;
      }
      if (data[0].type === 'track') {
        return <TopTracksList tracks={data} />;
      }
    }

    // Default JSON view for other data
    return (
      <pre style={{ 
        maxHeight: '400px', 
        overflow: 'auto', 
        backgroundColor: '#f5f5f5', 
        padding: '1rem' 
      }}>
        {JSON.stringify(data, null, 2)}
      </pre>
    );
  };

  const renderOptions = () => {
    if (!data) return null;

    if (Array.isArray(data) && data.length > 0) {
      // Show FetchOptions only for top artists or tracks
      if (data[0].type === 'artist' || data[0].type === 'track') {
        return (
          <FetchOptions 
            limit={limit} 
            timeRange={timeRange} 
            onLimitChange={setLimit} 
            onTimeRangeChange={setTimeRange} 
          />
        );
      }
      // Show RecentlyPlayedOptions only for recently played tracks
      if (data[0].track) {
        return (
          <RecentlyPlayedOptions
            limit={recentLimit}
            before={beforeDate}
            after={afterDate}
            onLimitChange={setRecentLimit}
            onBeforeChange={setBeforeDate}
            onAfterChange={setAfterDate}
          />
        );
      }
    }
    return null;
  };

  return (
    <div style={{ padding: '2rem' }}>
      <ProfileHeader profile={profile} />
      <h1>Spotify API Explorer</h1>

      <div style={{ display: 'grid', gap: '1rem', marginBottom: '2rem' }}>
        <LoadingButton 
          onClick={() => fetchData('/top-artists', { limit, time_range: timeRange })} 
          loading={loading}
        >
          Get Top Artists
        </LoadingButton>

        <LoadingButton 
          onClick={() => fetchData('/top-tracks', { limit, time_range: timeRange })} 
          loading={loading}
        >
          Get Top Tracks
        </LoadingButton>

        <LoadingButton 
          onClick={() => fetchData('/recently-played', { 
            limit: recentLimit,
            before: beforeDate && new Date(beforeDate).getTime(),
            after: afterDate && new Date(afterDate).getTime()
          })} 
          loading={loading}
        >
          Get Recently Played
        </LoadingButton>

        <LoadingButton 
          onClick={() => fetchData('/playlists', { limit: 20, offset: 0 })} 
          loading={loading}
        >
          Get Playlists
        </LoadingButton>
      </div>

      {error && (
        <div style={{ color: 'red', marginBottom: '1rem' }}>
          Error: {error}
        </div>
      )}

      {renderOptions()}
      {renderData()}

      <button 
        onClick={logout}
        style={{ 
          marginTop: '2rem',
          padding: '0.5rem 1rem',
          backgroundColor: '#ff4444',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        Logout
      </button>
    </div>
  );
}

export default App;
