import React, { useState, useEffect } from 'react';
import { useAuthStatus } from './hooks/useAuthStatus';
import LoadingButton from './components/LoadingButton';

const BACKEND_URL = 'http://127.0.0.1:3001';

function App() {
  const { loggedIn, logout } = useAuthStatus();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isRedirecting, setIsRedirecting] = useState(false);

  useEffect(() => {
    if (loggedIn === false && !isRedirecting) {
      setIsRedirecting(true);
      window.location.href = `${BACKEND_URL}/auth/login`;
    }
  }, [loggedIn, isRedirecting]);

  const fetchData = async (endpoint, params = {}) => {
    setLoading(true);
    setError(null);
    try {
      const queryString = new URLSearchParams(params).toString();
      const url = `${BACKEND_URL}/api${endpoint}${queryString ? `?${queryString}` : ''}`;
      const response = await fetch(url, { credentials: 'include' });
      const result = await response.json();
      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      const response = await fetch(`${BACKEND_URL}/auth/logout`, {
        method: 'POST',
        credentials: 'include'
      });

      if (!response.ok) {
        throw new Error('Logout failed');
      }

      // Clear any local state
      setData(null);
      setError(null);
      setIsRedirecting(true);

      // Redirect to login page
      window.location.href = `${BACKEND_URL}/auth/login`;
    } catch (error) {
      setError('Logout failed. Please try again.');
    }
  };

  // Show loading state while checking auth
  if (loggedIn === null) {
    return <div>Loading...</div>;
  }

  // Don't render anything while redirecting
  if (!loggedIn || isRedirecting) {
    return null;
  }

  const renderData = () => {
    if (!data) return null;
    return (
      <pre style={{ 
        maxHeight: '400px', 
        overflow: 'auto', 
        backgroundColor: '#f5f5f5', 
        padding: '1rem' 
      }}>
        {JSON.stringify(data, null, 2)}
      </pre>
    );
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Spotify API Explorer</h1>

      <div style={{ display: 'grid', gap: '1rem', marginBottom: '2rem' }}>
        <LoadingButton 
          onClick={() => fetchData('/profile')} 
          loading={loading}
        >
          Get Profile
        </LoadingButton>

        <LoadingButton 
          onClick={() => fetchData('/top-artists')} 
          loading={loading}
        >
          Get Top Artists
        </LoadingButton>

        <LoadingButton 
          onClick={() => fetchData('/top-tracks', { limit: 20, time_range: 'long_term' })} 
          loading={loading}
        >
          Get Top Tracks
        </LoadingButton>

        <LoadingButton 
          onClick={() => fetchData('/recently-played', { limit: 50 })} 
          loading={loading}
        >
          Get Recently Played
        </LoadingButton>

        <LoadingButton 
          onClick={() => fetchData('/playlists', { limit: 20, offset: 0 })} 
          loading={loading}
        >
          Get Playlists
        </LoadingButton>
      </div>

      {error && (
        <div style={{ color: 'red', marginBottom: '1rem' }}>
          Error: {error}
        </div>
      )}

      {renderData()}

      <button 
        onClick={handleLogout} 
        style={{ 
          marginTop: '2rem',
          padding: '0.5rem 1rem',
          backgroundColor: '#ff4444',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        Logout
      </button>
    </div>
  );
}

export default App;
