import React from 'react';

const RecentlyPlayedOptions = ({ limit, before, after, onLimitChange, onBeforeChange, onAfterChange }) => {
  const [filterType, setFilterType] = React.useState('none'); // 'none', 'before', or 'after'

  const handleFilterTypeChange = (type) => {
    setFilterType(type);
    // Clear both dates when changing filter type
    onBeforeChange('');
    onAfterChange('');
  };

  return (
    <div style={{ marginBottom: '1rem', display: 'flex', flexDirection: 'column', gap: '1rem' }}>
      <div>
        <label style={{ marginRight: '0.5rem' }}>Items to show:</label>
        <select 
          value={limit} 
          onChange={(e) => onLimitChange(Number(e.target.value))}
          style={{ padding: '0.25rem' }}
        >
          <option value={10}>10</option>
          <option value={20}>20</option>
          <option value={50}>50</option>
        </select>
      </div>

      <div>
        <label style={{ marginRight: '0.5rem' }}>Filter type:</label>
        <select 
          value={filterType}
          onChange={(e) => handleFilterTypeChange(e.target.value)}
          style={{ padding: '0.25rem' }}
        >
          <option value="none">No date filter</option>
          <option value="before">Before date</option>
          <option value="after">After date</option>
        </select>
      </div>

      {filterType === 'before' && (
        <div>
          <label style={{ marginRight: '0.5rem' }}>Before:</label>
          <input 
            type="datetime-local" 
            value={before} 
            onChange={(e) => onBeforeChange(e.target.value)}
            style={{ padding: '0.25rem' }}
          />
        </div>
      )}

      {filterType === 'after' && (
        <div>
          <label style={{ marginRight: '0.5rem' }}>After:</label>
          <input 
            type="datetime-local" 
            value={after} 
            onChange={(e) => onAfterChange(e.target.value)}
            style={{ padding: '0.25rem' }}
          />
        </div>
      )}
    </div>
  );
};

export default RecentlyPlayedOptions;
