import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  Link
} from 'react-router-dom';

function LoginPage() {
  return (
    <div>
      <h2>You are not logged in.</h2>
      <a href="https://9369677ba549.ngrok-free.app/auth/login">
        <button>Login with Spotify</button>
      </a>
    </div>
  );
}

function Home() {
  return (
    <div>
      <h1>Welcome, you are logged in!</h1>
      <p>You can now explore Spotify features.</p>
      <Link to="/">Log out</Link>
    </div>
  );
}

function App() {
  const [loggedIn, setLoggedIn] = useState(null);

  useEffect(() => {
    fetch('https://9369677ba549.ngrok-free.app/api/auth/status', { credentials: 'include' })
      .then(res => res.json())
      .then(data => setLoggedIn(data.loggedIn))
      .catch(() => setLoggedIn(false));
  }, []);

  if (loggedIn === null) return <p>Loading...</p>;

  return (
    <Router>
      <Routes>
        {/* If logged in, go to /home, else show login page */}
        <Route
          path="/"
          element={loggedIn ? <Navigate to="/home" replace /> : <LoginPage />}
        />
        {/* If logged in, show Home, else redirect to login */}
        <Route
          path="/home"
          element={loggedIn ? <Home /> : <Navigate to="/home" replace />}
        />
      </Routes>
    </Router>
  );
}

export default App;
