import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

function LoginPage() {
  return (
    <div>
      <h2>You are not logged in.</h2>
      <a href="https://9369677ba549.ngrok-free.app/auth/login">
        <button>Login with Spotify</button>
      </a>
    </div>
  );
}

function Home() {
  const query = useQuery();
  const tokenFromUrl = query.get('access_token');
  const [token, setToken] = useState(localStorage.getItem('spotify_token') || tokenFromUrl);

  useEffect(() => {
    if (tokenFromUrl) {
      localStorage.setItem('spotify_token', tokenFromUrl);
      setToken(tokenFromUrl);
    }
  }, [tokenFromUrl]);

  return (
    <div>
      <h1>Welcome!</h1>
      <p>Token: {token}</p>
    </div>
  );
}

export default function App() {
  const token = localStorage.getItem('spotify_token');

  return (
    <Router>
      <Routes>
        <Route path="/" element={token ? <Navigate to="/home" replace /> : <LoginPage />} />
        <Route path="/home" element={token ? <Home /> : <Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}
